package day3;

public class OperatorEx06 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b,c;
		c=b=a;
		System.out.printf("%d %d %d",a,b,c);
		a=a+10;
		a+=10;
		
		b=b-5;
		b-=5;
		
		c=c*8;
		c*=8;
		
		c=c/2;
		c/=2;
		
		c=c%5;
		c%=5;
		
		
	}

}
